function next_Page() {

	news_block = '';
	for (var key in news) {
		if (key > 6) {

			news_block += '<div class="shadow-2xl h-auto rounded-2xl overflow-hidden w-full md:w-96 bg-white items-center flex flex-col"><div class="relative w-full h-80 md:h-64 lg:h-44"><img src="img/' + news[key].img + '" alt="mug." class="w-full h-full object-center object-cover">';
			news_block += '</div><div class="bg-white py-2 px-5  flex flex-col h-auto"><h5 class="text-xl font-semibold mb-2">' + news[key].title + '</h5><p class="mb-2 text-xl">';
			news_block += '' + news[key].text + '</p></div><div class="mt-auto mb-5"><button onclick="new_Win(' + "'" + news[key].title + "','" + news[key].img + "','" + news[key].date + "','" + news[key].big_text_p1 + "','" + news[key].big_text_p2 + "'" + ')" class="uppercase mx-auto  font-bold rounded-2xl transition duration-500 ease-in-out bg-yellow-600  text-gray-100 hover:text-gray-800  transform hover:scale-110 py-2 px-4  my-3">Читать</button></div></div>';
		}

	}

	document.getElementById('news_block').innerHTML = news_block;
	document.getElementById('page_button').innerHTML = '<i class="fa fa-angle-double-left fa-lg"></i>';
	document.getElementById('page_button').removeEventListener("click", next_Page, false);
	document.getElementById('page_button').addEventListener("click", fist_Page, false);

}
window.onload = function () {
	fist_Page();
	main_page_news_block();

};



